class StringUtils {
  static String filterStrBfParse(String str) {
    if (str.isNotEmpty != true) return '';
    String result = '';
    int i;
    for (i = 0; i < str.length; i++) {
      String tt = str.substring(i, i + 1);

      if (tt.isNotEmpty) {
        final ascII = tt.codeUnitAt(0);
        if (ascII <= 31) {
          tt = '';
        }
        if (ascII == 4) {
          tt = "'|'";
        } // -- EOT
        result = result + tt;
      }
    }
    return result;
  }

  static String paresStringToHtml(String str, {bool isTransformBr = true}) {
    if (str.isEmpty) return str;
    String result = '', stringSlug = str;
    final exp = RegExp('/\r/\n|\r|\n');
    stringSlug = stringSlug.replaceAll(exp, isTransformBr ? '</br>' : '');
    final exp2 = RegExp('/\\\\r/\\\\n|\\\\r|\\\\n|\\\\t');
    stringSlug = stringSlug.replaceAll(exp2, isTransformBr ? '</br>' : '');
    for (int i = 0; i < stringSlug.length; i++) {
      String tt = stringSlug[i];
      final ascII = tt.codeUnitAt(0);
      if (ascII <= 31) {
        tt = '';
      }
      if (ascII == 4) {
        tt = "'|'";
      } // -- EOT
      result = result + tt;
    }
    return result;
  }

  static bool checkSensitiveWord(String str) {
    return arraySensitiveWords.any((word) => str.toLowerCase().contains(word.toLowerCase()));
  }

  // static bool checkAddressNotContainSpecialChar(String str) {
